const store = require('../store')

test('input must be a buffer', async () => {
  await expect(store('testingThis'))
    .rejects
    .toThrow('input must be a buffer')
})

test('responds with data id length of 4', async () => {
  const value = Buffer.from('', 'hex')
  const data = await store(value)
  expect(Buffer.isBuffer(value)).toBeTruthy()
  expect(data.id.length).toBe(4)
})

